<script>
import { useRouter } from 'vue-router'
import gsap from 'gsap'

export default {
  setup () {
    const router = useRouter()

    const moveToMyworks = () => {
      router.push({
        name: 'Myworks'
      })
    }
    const moveToHistory = () => {
      router.push({
        name: 'History'
      })
    }
    const moveToAboutMe = () => {
      router.push({
        name: 'AboutMe'
      })
    }
    const increleft1 = () => {
      gsap.to('.hoverleft1', {
        duration: 0.3, opacity: 1, cursor: 'pointer', visibility: 'visible', delay: 0.3
      })
    }
    const increleft2 = () => {
      gsap.to('.hoverleft2', {
        duration: 0.3, opacity: 1, cursor: 'pointer', visibility: 'visible', delay: 0.3
      })
    }
    const increright = () => {
      gsap.to('.hoverright', {
        duration: 0.3, opacity: 1, cursor: 'pointer', visibility: 'visible', delay: 0.3
      })
    }
    const decreleft = () => {
      gsap.to('.hoverleft1, .hoverleft2, .hoverright', {
        duration: 0.3, opacity: 0, cursor: 'default', delay: 0.3
      })
      gsap.to('.hoverleft1, .hoverleft2, .hoverright', {
        duration: 0, delay: 0.6
      })
    }
    const decreright = () => {
      gsap.to('.hoverleft1, .hoverleft2, .hoverright', {
        duration: 0.3, opacity: 0, cursor: 'default', delay: 0.3
      })
      gsap.to('.hoverleft1, .hoverleft2, .hoverright', {
        duration: 0, delay: 0.6
      })
    }
    const stop = () => {
      document.querySelector('.rod').style.animationPlayState = 'paused'
    }
    const resume = () => {
      document.querySelector('.rod').style.animationPlayState = 'running'
    }
    return {
      stop,
      resume,
      increleft1,
      increleft2,
      increright,
      decreleft,
      decreright,
      moveToMyworks,
      moveToHistory,
      moveToAboutMe
    }
  }
}
</script>

<style scoped>
.hover {
  position: absolute;
  cursor: pointer;
  opacity: 0.5;
  transition: 0s;
}
.hover:hover {
  opacity: 1;
  transition-duration: 0.3s;
}
.hoverleft1, .hoverleft2, .hoverright {
  position: absolute;
  opacity: 0;
}
.rod {
  width: 100%;
  height: 70%;
  top: 0;
  position: absolute;
  transform-origin: 50% 0%;
  animation-name: shake;
  animation-duration: 6s;
  animation-iteration-count: infinite;
}
@keyframes shake{
  30%{
  transform: rotate(-2deg);
  }
  70%{
  transform: rotate(2deg);
  }
}
</style>
