<script>
import { gsap, Power1, Quad } from 'gsap'
import { onMounted } from 'vue'
import { useRouter } from 'vue-router'

export default {
  setup () {
    const router = useRouter()
    const shake = gsap.timeline()
    onMounted(() => {
      shake.fromTo('.rod', { rotate: 3, ease: Power1.easeInOut }, { transformOrigin: '50% 0%', rotate: -3, duration: 4, ease: Power1.easeInOut, repeat: -1, yoyo: true })
      const showyellow = gsap.from('#square1', { transformOrigin: 'right', scaleX: 0.08, duration: 1, ease: Quad.easeInOut })
      showyellow.pause()
      document.querySelector('#yellow1').addEventListener('mouseenter', function () {
        showyellow.play()
      })
      document.querySelector('#yellow1').addEventListener('mouseleave', function () {
        showyellow.reverse()
      })
      const showgreen = gsap.from('#square2', { transformOrigin: 'left', scaleX: 0.08, duration: 1, ease: Quad.easeInOut })
      showgreen.pause()
      document.querySelector('#green2').addEventListener('mouseenter', function () {
        showgreen.play()
      })
      document.querySelector('#green2').addEventListener('mouseleave', function () {
        showgreen.reverse()
      })
      const showorange = gsap.from('#square3', { transformOrigin: 'right', scaleX: 0.08, duration: 1, ease: Quad.easeInOut })
      showorange.pause()
      document.querySelector('#orange3').addEventListener('mouseenter', function () {
        showorange.play()
      })
      document.querySelector('#orange3').addEventListener('mouseleave', function () {
        showorange.reverse()
      })
    })

    const shakestop = () => { shake.pause() }
    const shakeresume = () => { shake.resume() }
    const moveToOnPower = () => {
      router.push({
        name: 'Home'
      })
    }
    const moveToWeather = () => {
      router.push({
        name: 'Weather'
      })
    }
    const moveToDarkmode = () => {
      router.push({
        name: 'Darkmode'
      })
    }
    return { shakestop, shakeresume, moveToOnPower, moveToWeather, moveToDarkmode }
  }
}
</script>

<style scoped>
.rod {
  z-index: 4;
}
#yellow1, #green2, #orange3 {
  cursor: pointer;
}
</style>
